class LegacyPrinter {
  printOldFormat(text: string) {
    console.log(`[OLD] ${text}`);
  }
}

interface ModernPrinter {
  print(text: string): void;
}

class PrinterAdapter implements ModernPrinter {
  constructor(private legacy: LegacyPrinter) {}

  print(text: string) {
    this.legacy.printOldFormat(text);
  }
}

// Usage: make incompatible interfaces work together
const adapter: ModernPrinter = new PrinterAdapter(new LegacyPrinter());
adapter.print('Hello');
